const expect = require('chai').expect;
const path = require('path');
const fs = require('fs');
const {mapCollectionToGamesList, convertThumbnailToDataUri} = require("../src/bggAdapter");


describe('BGG Adapter', () => {
    describe('convertThumbnailToDataUri', () => {
        it('does not convert empty image', async () => {
            const original = {
                image: ""
            };
            const actual = await convertThumbnailToDataUri(original);
            expect(actual).to.eql(original);
        });
        it('does not convert already converted image', async () => {
            const original = {
                image: "data:image/jpeg;base64,"
            };
            const actual = await convertThumbnailToDataUri(original);
            expect(actual).to.eql(original);
        });
        it('converts thumbnail to data uri', async () => {
            const actual = await convertThumbnailToDataUri({
                image: "https://cf.geekdo-images.com/thumb/img/NpZjJd2NgxSJV2WrlB_U1e89txY=/fit-in/200x150/pic354500.jpg"
            });
            expect(actual).to.eql({
                image: "data:image/jpeg;base64,"
            });
        });
    });

    describe('mapCollectionToGamesList', () => {
        it('maps invalid collection to empty games list', async () => {
            let actual = await mapCollectionToGamesList("invalid");
            expect(actual).to.eql([]);
        });
        it('maps collection to games list', async () => {
            const xml = fs.readFileSync(path.join(__dirname, '__mocks/bgg-collection-single-item-response.xml'), 'utf8');
            const gamesList = await mapCollectionToGamesList(xml);
            expect(gamesList).to.have.lengthOf(1);
            expect(gamesList[0].title).to.eq('Battlestar Galactica: The Board Game');
            expect(gamesList[0].image).to.eq("data:image/jpeg;base64,");
            expect(gamesList[0].minPlayers).to.eq(3);
            expect(gamesList[0].maxPlayers).to.eq(6);
            expect(gamesList[0].playingTime).to.eq(300);
        });
    });
});
